import React, { Component } from 'react';
import MorningRoutine from '../components/MorningRoutine';
import EveningRoutine from '../components/EveningRoutine'
import {Link} from 'react-router-dom'
import {Button, Divider, Container } from 'semantic-ui-react'
import API from '../adapters/API';

const DashboardContainer = () => {

  const  getRoutine = () => {
        if (!this.state) {this.props.history.push("/welcome")}
       let currentTime = this.getTime()
       if (currentTime >= 0 &&currentTime < 12) {
    return <MorningRoutine getRoutine = {this.props.morningRoutine} user = {this.props.user}/> }
       else if (currentTime >=12) {
           return <EveningRoutine user = {this.props.user}/>}

    }

    const routine = this.getRoutine()
    return (
        <div>
            {routine}
        </div>
    );
}

export default DashboardContainer;

