import React, { Component } from 'react';

export class EveningRoutine extends Component {

    getName = () => {
        if (this.props.user) {
        let name = this.props.user.display_name.charAt(0).toUpperCase()+ this.props.user.display_name.slice(1)
        return name 
        } else {console.log(this)}

    }


    render() {
        return (
            <div>
                 {this.props.user ? <h1 className="main-title">Welcome, {this.props.user.display_name}</h1> :<h1 className="main-title">Welcome!</h1>}
            </div>
        );
    }
}

export default EveningRoutine
