import React, { Component } from "react";

export class EveningRoutine extends Component {
  greeting = () => {
    this.props.user ? (
      <h1 className="main-title">
        Good afternoon,{" "}
        {this.props.user.display_name.charAt(0).toUpperCase() +
          this.props.user.display_name.slice(1)}
        !
      </h1>
    ) : (
      <h1 className="main-title">Good afternoon!</h1>
    );
  };

  render() {
    return <div></div>;
  }
}

export default EveningRoutine;
