import React, { Component } from 'react';

export class EveningRoutine extends Component {




    render() {
        return (
            <div>
  {this.props.user ? <h1 className="main-title">Good evening, {this.props.user.display_name.charAt(0).toUpperCase()+ this.props.user.display_name.slice(1)}!</h1> :<h1 className="main-title">Good evening!</h1>}

            </div>
        );
    }
}

export default EveningRoutine
