const BASE_URL = "http://localhost:3000/"

const LOGIN_URL = `${BASE_URL}api/v1/login`
const SIGNUP_URL = `${BASE_URL}api/v1/signup`
const VALIDATE_URL = `${BASE_URL}api/v1/validate`


const headers = (more = {}) => ({
    'Content-Type': 'application/json',
    Accept: 'application/json',
    ...more
  })
  const authHeader = (more = {}) => ({
    Authorisation: localStorage.getItem('token'),
    ...more
  })

  const handleError = () => {
    console.error('something went wrong')
  }

  const handleServerResponse = res => {
    // console.log(res => res.json())
    if (res.ok) {
      return res.text().then(text => {
        try {
          return JSON.parse(text)
        } catch (error) {
          return { staticPageContent: text }
        }
      })
    } else if (res.status === 503) {
      return { code: 503 }
    } else if (res.status === 500) {
      return { code: 500, error: 'IDEK what is going on ' }
    } else {
      return res.text().then(text => {
        try {
          return JSON.parse(text)
        } catch (error) {
          return res
        }
      })
    }
  }

  const signup = userDetails =>
  fetch(SIGNUP_URL, {
  method: 'POST',
  headers: headers(),
  body: JSON.stringify({ user: userDetails })
  })
  .then(handleServerResponse)
  .then(userDetails => {

      if (userDetails.token) {
        localStorage.setItem('token', userDetails.token)
      }
      return userDetails.user
})
.catch(handleError)

const login = userDetails =>
  fetch(LOGIN_URL, {
    method: 'POST',
    headers: headers(),
    body: JSON.stringify({ user: userDetails })
  })
    .then(handleServerResponse)
    .then(userDetails => {
      if (userDetails.errors) {
        alert("Nope")
      }
      // if (userDetails.token) {
      //   localStorage.setItem('token', userDetails.token)
      // }
      // return userDetails.user
    })
    .catch(handleError)

  const validateUser = () => 
    fetch(VALIDATE_URL, {
      method: 'POST',
      headers: headers(authHeader())
    })
      .then(handleServerResponse)
      .then(userDetails => {
        // console.log(userDetails)})
        if (!userDetails) {
          return { errors: ['something went wrong '] }
        }
        if (userDetails.token) {
          localStorage.setItem('token', userDetails.token)
        }
        return userDetails.user || userDetails
      })
      .catch(handleError)


  const logout = () => {
    localStorage.removeItem('token')
  }
  export default {
    login,
    signup,
    validateUser,
    logout
  }
